mapper.select:
select * from (select rownum as rnum, mem.* from (select member_no as memberNo, member_id as memberId, member_nick as memberNick, member_phone as memberPhone, grade, member_point as memberPoint, member_reportcount as memberReportCount, enrolldate as enrollDate from member_tbl
where grade=#{selectNum }
where grade=1 or grade=2 or grade=3
order by member_no desc) mem) where rnum between #{start} and #{end}

select count(*) from member_tbl

	
		where grade=#{selectNum }
	
	
		where grade=1 or grade=2 or grade=3

select * from (select rownum as rnum, mem.* from (select member_no as memberNo, member_id as memberId, member_nick as memberNick, member_phone as memberPhone, grade, member_point as memberPoint, member_reportcount as memberReportCount, enrolldate as enrollDate from member_tbl
where member_id like '%'||#{keyword}||'%'
where member_nick like '%'||#{keyword}||'%'
where member_no = #{keyword }
order by member_no desc) mem) where rnum between #{start} and #{end}

select * from (select rownum as rnum, pay.* from (select pay_no as payNo, (select member_Id from member_tbl m where payment.member_no = m.member_no) as memberId, product_no as productNo, pay_state as payState, pay_date as payDate, payment as payment, pay_method as payMethod, pay_usepoint as payUsepoint, pay_addpoint as payAddpoint from payment_tbl payment
where pay_state = #{selectNum }
where pay_state = 1 or pay_state = 2 or pay_state = 3
order by pay_no desc) pay) where rnum between #{start} and #{end}

select * from (select rownum as rnum, pay.* from (select pay_no as payNo, (select member_Id from member_tbl m where payment.member_no = m.member_no) as memberId, pay_state as payState, pay_date as payDate, payment as payment, pay_method as payMethod, pay_usepoint as payUsepoint, pay_addpoint as payAddpoint from payment_tbl payment
where pay_no = #{keyword }
where member_no in (select member_no from member_tbl where member_id like '%'||#{keyword }||'%')
order by pay_no desc) pay) where rnum between #{start} and #{end}

select count(*) from payment_tbl

	
		where pay_state=#{selectNum }
	
	
		where pay_state=1 or pay_state=2 or pay_state=3

select count(*) from payment_tbl
where pay_no = #{keyword }
where member_no in (select member_no from member_tbl where member_id like '%'||#{keyword }||'%')

mapper.delete:
