		product_tbl where product_no = #{index }
	
	
		review_tbl where review_no = #{index }
	
	
		request_tbl where req_no = #{index }
	
	
		community_tbl where comm_no = #{index }
	
	
		communityco_tbl where cmnt_no = #{index }

