mapper.select:
select * from (select rownum as rnum, mem.* from (select member_no as memberNo, member_id as memberId, member_nick as memberNick, member_phone as memberPhone, grade, member_point as memberPoint, member_reportcount as memberReportCount, enrolldate as enrollDate from member_tbl
where grade=#{selectNum }
where grade=1 or grade=2 or grade=3
order by member_no desc) mem) where rnum between #{start} and #{end}

select count(*) from member_tbl

	
		where grade=#{selectNum }
	
	
		where grade=1 or grade=2

select * from (select rownum as rnum, mem.* from (select member_no as memberNo, member_id as memberId, member_nick as memberNick, member_phone as memberPhone, grade, member_point as memberPoint, member_reportcount as memberReportCount, enrolldate as enrollDate from member_tbl
where member_id like '%'||#{keyword}||'%'
where member_nick like '%'||#{keyword}||'%'
where member_no = #{keyword }
order by member_no desc) mem) where rnum between #{start} and #{end}

select * from (select rownum as rnum, pay.* from (select pay_no as payNo, (select member_Id from member_tbl m where payment.member_no = m.member_no) as memberId, product_no as productNo, pay_state as payState, pay_date as payDate, payment as payment, pay_method as payMethod, pay_usepoint as payUsepoint, pay_addpoint as payAddpoint from payment_tbl payment
where pay_state = #{selectNum }
where pay_state = 1 or pay_state = 2 or pay_state = 3
order by pay_no desc) pay) where rnum between #{start} and #{end}

select * from (select rownum as rnum, pay.* from (select pay_no as payNo, (select member_Id from member_tbl m where payment.member_no = m.member_no) as memberId, pay_state as payState, pay_date as payDate, payment as payment, pay_method as payMethod, pay_usepoint as payUsepoint, pay_addpoint as payAddpoint from payment_tbl payment
where pay_no = #{keyword }
where member_no in (select member_no from member_tbl where member_id like '%'||#{keyword }||'%')
order by pay_no desc) pay) where rnum between #{start} and #{end}

select count(*) from payment_tbl

	
		where pay_state=#{selectNum }
	
	
		where pay_state=1 or pay_state=2 or pay_state=3

select count(*) from payment_tbl
where pay_no = #{keyword }
where member_no in (select member_no from member_tbl where member_id like '%'||#{keyword }||'%')

select count(*)
	from 
	
		
			product_tbl where product_no = #{reportIndex }
		
		
			review_tbl where review_no = #{reportIndex }
		
		
			request_tbl where req_no = #{reportIndex }
		
		
			community_tbl where comm_no = #{reportIndex }
		
		
			communityco_tbl where cmnt_no = #{reportIndex }

mapper.delete:
delete from 

	
		product_tbl where product_no = #{index }
	
	
		review_tbl where review_no = #{index }
	
	
		request_tbl where req_no = #{index }
	
	
		community_tbl where comm_no = #{index }
	
	
		communityco_tbl where cmnt_no = #{index }

mapper.update:
update report_tbl set 
report_is = #{selNo }
where report_no = #{reportNo }

mapper.select:
select * from (select rownum as rnum, faq.* from (select faq_no as fAQNo, faq_title as FAQTitle, faq_type as FAQType, faq_date as FAQDate from faq_tbl
where faq_type = #{selectNum }
order by faq_no desc) faq) where rnum between #{start} and #{end}

select * from (select rownum as rnum, faq.* from (select faq_no as fAQNo, faq_title as FAQTitle, faq_type as FAQType, faq_date as FAQDate from faq_tbl
where faq_title like '%'||#{keyword}||'%'
order by faq_no desc) faq) where rnum between #{start} and #{end}

mapper.update:
update faq_tbl set 
faq_type=#{FAQType}, faq_title=#{FAQTitle}, faq_content=#{FAQContent} 
where faq_no = #{FAQNo}

update notice_tbl set 
notice_title=#{noticeTitle}, notice_content=#{noticeContent} 
where notice_no = #{noticeNo}

mapper.select:
select * from (select rownum as rnum, ans.* from (select ans_no as ansNo, ans_title as ansTitle, (select ans_state from question_tbl where answer_tbl.qst_no = question_tbl.qst_no) as ansState, ans_date as ansDate from answer_tbl
where (select ans_state from question_tbl where answer_tbl.qst_no = question_tbl.qst_no) = #{selectNum}
order by ans_no desc) ans) where rnum between #{start} and #{end}

select count(*) from
answer_tbl

	where (select ans_state from question_tbl where answer_tbl.qst_no = question_tbl.qst_no) = #{selectNum}

select * from (select rownum as rnum, qst.* from (select qst_no as qstNo, qst_title as qstTitle, ans_state as ansState, qst_date as qstDate from question_tbl
where ans_state = 0
order by qst_no desc) qst) where rnum between #{start} and #{end}

select count(*) from
question_tbl

	where member_no = #{memberNo }

select * from (select rownum as rnum, exp.* from (select expert_no as expertNo, expert_name as expertName, expert_category as expertCategory, job_name as jobName, expert_app as expertApp from expert_tbl
where expert_app = #{selectNum }-1
order by expert_no desc) exp) where rnum between #{start} and #{end}

select * from (select rownum as rnum, exp.* from (select expert_no as expertNo, expert_name as expertName, expert_category as expertCategory, job_name as jobName, expert_app as expertApp from expert_tbl
where expert_name like '%'||#{keyword}||'%'
where expert_category like #{keyword}
order by expert_no desc) exp) where rnum between #{start} and #{end}

select count(*) from expert_tbl

	
		where expert_name like '%'||#{keyword}||'%'
	
	
		where expert_category like #{category}

select expert_no as expertNo, expert_category as expertCategory, expert_tag as expertTag, credential as credential, issuing_authority as issuingAuthority, acquistion_date as acquistionDate, certificate_path as certificatePath,
expert_name as expertName, job_name as jobName, expert_phone as expertPhone, expert_email as expertEmail, expert_app as expertApp
from expert_tbl where expert_no = #{value }
