mapper.update:
update member_tbl set 
	member_id =#{memberId}, 
	member_nick =#{memberNick}, 
	member_pw=#{memberPw}, 
	member_Phone = #{memberPhone} 		 
	where member_no=#{memberNo}

 		 update into payment_tbl set
 		 pay_state = 3

		where member_no=#{payNo}

mapper.select:
select pay_no as payNo, product_no as productNo, pay_date as payDate, payment, pay_usepoint as payUsepoint, pay_addpoint as payAddpoint
from payment_tbl
where pay_no=#{payNo}
