mapper.update:
update member_tbl set
member_picturename =#{memberPictureName}, member_picturepath =#{memberPicturePath},
member_nick=#{memberNick}, member_pw=#{memberPw}, member_Id=#{memberId}, member_point=#{memberPoint}, gender=#{gender}, birth=#{birth}, agreement=#{agreement}, grade=#{grade}, enrollDate=#{enrollDate},
member_Phone=#{memberPhone}
where member_no=#{memberNo}

update product_tbl set
product_title=#{productTitle}, product_Category=#{productCategory}, product_Intro=#{productIntro}, product_Tag=#{productTag}, product_Content=#{productContent}, cost=#{cost}, product_Qst=#{productQst}, product_Ans=#{productAns}, product_Addr=#{productAddr},
product_Option=#{productOption}, product_ImgPath=#{productImgPath}, product_Imgename=#{productImgname}
where product_no=#{productNo}

 		 update payment_tbl set
 		 pay_state = '3'

		where pay_no=#{payNo}

mapper.select:
select pay_no as payNo, product_no as productNo, pay_date as payDate, payment, pay_usepoint as payUsepoint, pay_addpoint as payAddpoint
from payment_tbl
where pay_no=#{payNo}

 		select 
 			count(*)
		from member_tbl where member_nick = #{memberNick}

select
count(*)
from member_tbl where member_id = #{memberId}

select
count(*)
from member_tbl where member_phone = #{memberPhone}
