mapper.select:
select * from (select rownum as rnum, b.* from (select p.product_no as productNo, p.expert_no as expertNo, (select expert_name from expert_tbl m where p.expert_no = m.member_no) as expertName, (select member_picturepath from member_tbl m where p.expert_no = m.member_no) as memberPicturePath, p.product_Category as productCategory, p.product_title as productTitle, p.product_intro as productIntro, p.product_tag as productTag, cost, sale, (cost)*(sale)/100 as salecost, (select nvl(count(*),0) from review_tbl rev where p.product_no = rev.product_no) as revCount, (select ROUND(avg(nvl(review_star, 0)),1) from review_tbl rev where rev.product_no = p.product_no)as reviewStar, p.product_imgpath as productImgPath from product_tbl p
where product_Category=#{selPro} and product_Category=#{selPro}
where product_type = 2
order by p.product_no desc)b) where rnum between #{start} and #{end}
